class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        num_prev = t_prev = a = b = 0
        for num, t in zip(nums, target):
            num_diff = num - num_prev
            t_diff = t - t_prev
            if (d := num_diff - t_diff) > 0:
                a += d
            else:
                b += -d
            num_prev, t_prev = num, t
        return max(a, b)


