class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        for i in range(len(nums)):
            if nums[i] != target[i]:
                diff.append(nums[i] - target[i])
            else:
                diff.append(0)


        idx = 0
        ans = 0
        while idx < len(nums):
            if diff[idx] < 0:
                ans += (0 - diff[idx])
                maximum = diff[idx]
                for i in range(idx + 1, len(nums)):
                    if diff[i] < 0:
                        maximum = max(maximum, diff[i])
                        diff[i] -= maximum
                    else:
                        break
            elif diff[idx] > 0:
                ans += diff[idx]
                minimum = diff[idx]
                for i in range(idx + 1, len(nums)):
                    if diff[i] > 0:
                        minimum = min(minimum, diff[i])
                        diff[i] -= minimum
                    else:
                        break
            diff[idx] = 0

            idx += 1
        return ans
