class Solution {
    fun minimumOperations(nums: IntArray, target: IntArray): Long {
        val diff = IntArray(nums.size) {0};
        for (i in nums.indices) {
            diff[i] = nums[i] - target[i];
        }
        var last = 0;
        var count = 0L;
        for (num in diff) {
            if (last > 0) {
                if (num < last) {
                    count += last - maxOf(num, 0);
                }
            }
            else {
                if (num > last) {
                    count += minOf(num, 0) - last;
                }
            }
            last = num;
        }
        count += abs(last);
        return count;
    }
}
