class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(auto i = 0; i < n; ++i) nums[i] -= target[i];
        long long ops = abs(nums[0]);
        for(auto i = 1; i < n; ++i){
            if(nums[i]<0 == nums[i-1]<0){
                auto diff = abs(nums[i]) - abs(nums[i-1]);
                if(diff > 0) ops += diff;
            } else ops += abs(nums[i]);
        }
        return ops;
    }
};
