func minimumOperations(nums []int, target []int) int64 {
    n:=len(nums)
    arr:=make([]int,n)
    for i:=range nums{
        arr[i] = target[i]-nums[i]
    }
    var ans int
    var f func(pre,id int)
    f = func(pre,id int){
        if id==n{
            return
        }
        if arr[id]==0{
            f(0,id+1)
            return
        }
        if arr[id]>0{
            i:=id
            for i<n-1&&arr[i]<=arr[i+1]{
                i++
            }
            ans+=arr[i]-max(pre,0)
            for i<n-1&&arr[i+1]>0&&arr[i]>=arr[i+1]{
                i++
            }
            id = i
        }else{
            i:=id
            for i<n-1&&arr[i]>=arr[i+1]{
                i++
            }
            ans-=arr[i]-min(pre,0)
            for i<n-1&&arr[i+1]<0&&arr[i]<=arr[i+1]{
                i++
            }
            id = i
        }
        f(arr[id],id+1)
    }
    f(0,0)
    return int64(ans)
}
