class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> d;
        for(int i=0; i<nums.size(); i++){
            d.push_back(target[i] - nums[i]);
        }
        long long ans = 0;
        bool p = true;
        vector<int> cons;
        for(int i=0; i<d.size(); i++){
            int n = d[i];
            if(p == (n >= 0)){
                cons.push_back(abs(n));
            } else{
                p = (n >= 0);
                ans += proc(cons);
                cons.clear();
                cons.push_back(abs(n));
            }
        }
        ans += proc(cons);
        return ans;
    }

    long long proc(vector<int> cons){
        bool up = true;
        int last = 0;
        int v = 0;
        long long ans = 0;
        for(int i=0; i<cons.size(); i++){
            if(up && cons[i] < last){
                ans += last - v;
                up = false;
            }
            if(!up && cons[i] >= last){
                v = last;
                up = true;
            }
            last = cons[i];
        }
        if(up){
            ans += last - v;
        }
        return ans;
    }
};
