class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> v;
        int n=nums.size();
        for(int i=0;i<n;i++){
            v.push_back(target[i]-nums[i]);
        }
        long long int count=0;
        int temp=0;
        for(int i=0;i<n;i++){
           if(i==0){
            temp=v[i];
            count+=abs(v[i]);
           }
           if(temp==v[i]) continue;
           if(v[i] >= 0){
               if(temp <0) temp=0;
               if(v[i] > temp) {
                count+=(v[i]-temp);
                temp=v[i];
                }else{
                temp=v[i];

               }
           }else{
               if(temp>0) temp=0;
               if( v[i] < temp ){
                count+=abs(v[i]-temp);
                temp=v[i];
               }else{
                 temp=v[i];
               }
           }


        }
        return count;

    }
};
