class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for (int i = 0; i<nums.length; i++) {
            nums[i] = target[i] - nums[i];
        }
        long operations = 0;
        long inc = 0;
        inc = nums[0];
        operations = Math.abs(nums[0]);
        for (int i = 1; i<nums.length; i++) {
            if (nums[i] == 0) {
                inc = 0;
            } else if (nums[i] > 0) {
                if (inc > 0) {
                    if (nums[i] - inc <= 0) {
                        inc = nums[i];
                    } else {
                        operations += (nums[i] - inc);
                        inc = nums[i];
                    }
                } else {
                    inc = nums[i];
                    operations += nums[i];
                }
            } else {
                if (inc < 0) {
                    if (nums[i] - inc >= 0) {
                        inc = nums[i];
                    } else {
                        operations += (-1 * (nums[i] - inc));
                        inc = nums[i];
                    }
                } else {
                    inc = nums[i];
                    operations += (-1 * nums[i]);
                }
            }
        }
        return operations;
    }
}
