int N;
int A[1000000];
int B[1000000];
class Solution{
  public:
  long long minimumOperations(vector<int>& nums, vector<int>& target){
    int i;
    long long res = 0;
    N = nums.size();
    for(i=(0);i<(N);i++){
      A[i] = target[i] - nums[i];
    }
    A[N] = 0;
    B[0] = A[0];
    for(i=(1);i<(N+1);i++){
      B[i] = A[i] - A[i-1];
    }
    for(i=(0);i<(N+1);i++){
      if(B[i] > 0){
        res += B[i];
      }
    }
    return res;
  }
}
