class Solution {
public:
    bool sign(int number){
        return number >= 0; 
    }
    int solve(int last,int current){
        if(sign(last) == sign(current)){
            current = abs(current);
            last = abs(last);

            if(last > current) return 0;
            return current - last;
        }else{
            return abs(current);
        }
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> variance(nums.size());
        for(int index=0; index < nums.size() ; index++){
            variance[index] = target[index] - nums[index];
        }
        // for(auto ele : variance) cout<<ele<<" ";
        vector<int> pos(nums.size());
        pos[0] = abs(variance[0]);
        for(int index=1;index<pos.size();index++){
            pos[index] = solve(variance[index-1],variance[index]);
        }
        long long operations=0;
        for(auto oper : pos) operations += oper;
        return operations;
    }
};
