class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a - b for a, b in zip(nums, target)]
        print(diff)
        n = len(nums)

        ans = abs(diff[0])
        for i in range(1, n):
            if diff[i] * diff[i - 1] > 0:
                if diff[i] > 0:
                    ans += max(diff[i] - diff[i - 1], 0)
                else:
                    ans += max(diff[i - 1] - diff[i], 0)
            else:
                ans += abs(diff[i])
        return ans
