class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> ar(n);
        for(int i=0; i<n; i++){
            ar[i] = nums[i] - target[i];
        }
        long long ans = 0;
        int i = 0;
        while(i < n){
            if(ar[i] >= 0){
                ans += ar[i];
                i++;
                while(i < n && ar[i] >= 0){
                    ans += max(ar[i] - ar[i-1], 0);
                    i++;
                }
            }else{
                ans += abs(ar[i]);
                i++;
                while(i < n && ar[i] < 0){
                    ans += max(abs(ar[i]) - abs(ar[i-1]), 0);
                    i++;
                }
            }
        }
        return ans;
    }
};
