class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs_pos = [0] +[target[i] - nums[i] if target[i] - nums[i] >0 else 0 for i in range(len(nums))]
        diffs_neg = [0] +[target[i] - nums[i] if target[i] - nums[i] <0 else 0 for i in range(len(nums))]
        ret = 0

        for i in range(1, len(diffs_pos)):
            # Calculate the prefix sum of the differences.
            if diffs_pos[i] > diffs_pos[i-1]:
                ret +=  diffs_pos[i] - diffs_pos[i - 1]
            if diffs_neg[i] < diffs_neg[i-1]:
                ret +=  -(diffs_neg[i] - diffs_neg[i - 1])


        # print(diffs)

        return ret
