class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        for(int i=0;i<nums.size();){
            int d=nums[i]-target[i];
            res+=abs(d);
            i++;
            if(d>0){
                while(i<nums.size() && (nums[i]-target[i])>0){
                    if((nums[i]-target[i])>(nums[i-1]-target[i-1]))
                        res+=((nums[i]-target[i])-(nums[i-1]-target[i-1]));
                    i++;
                }                
            }
            else{
                while(i<nums.size() && (nums[i]-target[i])<0){
                    if(abs(nums[i]-target[i])>abs(nums[i-1]-target[i-1]))
                        res+=(abs(nums[i]-target[i])-abs(nums[i-1]-target[i-1]));
                    i++;
                } 
            }
        }
        return res;
    }
};
