class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = (int)nums.size();
        vector<int> d(n+2, 0);
        for (int i = 0; i < n; i++) d[i+1] = nums[i] - target[i];
        long long ans = 0;
        for (int i = 0; i < n+1; i++) ans += abs(d[i] - d[i+1]);
        return ans / 2;
    }
};
