class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long curinc=0;
        long long ans=0;
        for(auto i=0;i<nums.size();i++)
        {
            int a=nums[i],b=target[i];
            if(a+curinc==b) continue;
            int needed=b-a;
            if(curinc<0)
            {
                if(needed>0)
                {
                    ans+=needed;
                    curinc=needed;
                }
                else if(!needed)
                {
                    curinc=0;
                    continue;
                }
                else
                {
                    int cur=abs(curinc);
                    int need=abs(needed);
                    if(need>cur)
                    {
                        ans+=need-cur;
                        curinc=needed;
                    }
                    else
                    {
                        curinc=needed;
                    }
                }
            }
            else if(curinc==0)
            {
                if(needed)
                {
                    curinc=needed;
                    ans+=abs(needed);
                }
            }
            else//curinc>0
            {
                if(needed>0)
                {
                    if(curinc>needed)
                    {
                        curinc=needed;
                    }
                    else//curinc<needed
                    {
                        ans+=needed-curinc;
                        curinc=needed;
                    }
                }
                else if(!needed)
                {
                    curinc=0;
                    continue;
                }
                else//needed<0
                {
                    ans+=abs(needed);
                    curinc=needed;
                }
            }
            // cout<<i<<' '<<curinc<<' '<<ans<<endl;
        }
        return ans;
    }
};
