class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        typedef long long ll;

        ll r = 0;
        int N = nums.size();

        for(int i=0, j=1; i<N; ) {
            int d = t[i] - nums[i];

            if(d > 0) {
                int m = d;
                while(j<N && t[j]-nums[j] > 0) {
                    m = min(m, t[j]-nums[j]);
                    ++j;
                }
                r += abs(m);

                //printf("i:%d, j:%d, d:%d, m:%d\n", i, j, d, m);

                for(int k=i; k<j; ++k) {
                    nums[k] += m;
                }

                int k = i;
                while(k<j && t[k]-nums[k]==0) {
                    ++k;
                }                
                i=k, j=k+1;
            }
            else if(d<0) {
                int m = -d;
                while(j<N && nums[j]-t[j]>0) {
                    m = min(m, nums[j]-t[j]);
                    ++j;
                }
                r += abs(m);

                //printf("> i:%d, j:%d, d:%d, m:%d\n", i, j, d, m);                

                for(int k=i; k<j; ++k) {
                    nums[k] -= m;
                }

                int k = i;
                while(k<j && t[k]-nums[k]==0) {
                    ++k;
                }                
                i=k, j=k+1;
            }
            else {
                i = j++;
            }
        }

        return r;
    }
};
