class Solution
{
  public:
    long long minimumOperations(std::vector<int> &nums,
                                std::vector<int> &target)
    {
        long long former_change = 0;
        long long ans           = 0;

        for (int i = 0; i < nums.size(); i++) {
            long long tmp_change = nums[i] - target[i];
            if ((former_change ^ tmp_change) < 0) {
                former_change = tmp_change;
                ans += abs(tmp_change);
            } else {
                if (abs(tmp_change) > abs(former_change)) {
                    ans += abs(tmp_change - former_change);
                }
                former_change = tmp_change;
            }
        }

        return ans;
    }
};
