const int inf = 0x3f3f3f3f ;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size() ;

        vector<int> d(n) ;
        for(int i = 0 ; i < n ; ++ i)
        {
            d[i] = target[i] - nums[i] ;
            // cout << d[i] << ' ' ;
        }
        long long ans = 0 ;
        int j = abs(d[0]) ;
        int ok = 0 ;
        for(int i = 1 ; i < n ; ++ i)
        {
            if ( d[i] > 0 ) {
                if ( d[i - 1] > 0 ) {
                    if ( d[i] < d[i - 1] ) ok = 1 ;
                    if ( ok && d[i] > d[i - 1] ) {
                        // cout << i << " : " << j << '\n' ;
                        ans += j ; 
                        int t = d[i - 1] ;
                        for(int j = i ; j < n && d[j] > 0 ; ++ j)
                        {
                            t = min(t , d[j]) ;
                            d[j] -= t ;
                        }
                        j = d[i] ;
                        ok = 0 ;
                    } else {
                        j = max(j , abs(d[i])) ;
                    }
                } else {
                    ok = 0 ;
                    // cout << j << '\n' ;
                    ans += j ;
                    j = abs(d[i]) ;
                }
            } else if ( d[i] < 0 ) {
                if ( abs(d[i]) < abs(d[i - 1]) ) ok = 1 ;
                if ( d[i - 1] < 0) {
                    if ( ok && abs(d[i]) > (d[i - 1]) ) {
                        ans += j ;
                        int t = abs(d[i - 1]) ;
                        for(int j = i ; j < n && d[j] < 0 ; ++ j)
                        {
                            t = min(t , abs(d[j])) ;
                            d[j] += t ;
                        }
                        j = abs(d[i]) ;
                        ok = 0 ;
                    } else {
                        j = max(j , abs(d[i])) ;
                    }
                } else {
                    // cout << '\n' << j << '\n' ;
                    ans += j ;
                    ok = 0 ;
                    j = abs(d[i]) ;
                }
            } else {
                ans += j ;
                j = 0 ; 
                ok = 0 ;
            }
        }
        ans += j ;

        return ans ; 
    }
};
