class Solution {
public:
    long long calc(vector<int> v) {
        if(v.size()==0)return 0;
        long long res=abs(v[0]);
        for(int i = 1;i<v.size();i++) {
            if(abs(v[i]) > abs(v[i-1]))
                res+=abs(v[i]-v[i-1]);
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0;i<n;i++) {
            diff[i] = target[i]-nums[i];
        }
        long long ans=0;
        vector<int> tmp;
        tmp.push_back(diff[0]);
        for(int i = 1;i<n;i++) {
            if((diff[i] > 0) == (diff[i-1] > 0)) {
                tmp.push_back(diff[i]);
            }
            else {
                ans+=calc(tmp);
                tmp.clear();
                tmp.push_back(diff[i]);
            }
        }
        ans+=calc(tmp);
        return ans;
    }
};
