class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }
        long long ops = 0;
        long long curDiff = 0;
        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long addOps = abs(diff[i]) - abs(curDiff);
                if (addOps > 0) {
                    ops += addOps;
                }
                curDiff = diff[i];
            } else {
                ops += abs(diff[i]);
                curDiff = diff[i];
            }
        }
        return ops;
    }
};
