class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int>diffs;
        for(int i=0;i<n;i++){
            diffs.push_back(tar[i] - nums[i]);
        }
        long long ans = 0;
        ans += abs(diffs[0]);
        for(int i=1;i<n;i++){
            if(diffs[i] >0 and diffs[i-1] > 0 ){
                if(diffs[i] <= diffs[i-1]){
                    continue;
                }else{
                    ans+= diffs[i] - diffs[i-1];
                }
            }
            if(diffs[i] <0 and diffs[i-1] <0){
                if(diffs[i] >= diffs[i-1]){
                    continue;
                }else{
                    ans+= diffs[i-1] - diffs[i];
                }
            }

            if(diffs[i] >0 and diffs[i-1] <=0){
                ans+= diffs[i];
            }
            if(diffs[i]<0 and diffs[i-1] >=0){
                ans += -1*diffs[i];
            }
        }
        return ans;

    }
};
