class Solution:
    def find (self, n, diffArr):
        prevMin = [0]*n
        st = []
        for i in range(n):
            if diffArr[i] == 0:
                st = []
                continue
            while len(st) and diffArr[i] <= diffArr[st[-1]]:
                prevMin[st[-1]] = max(prevMin[st[-1]], diffArr[i])
                st.pop()

            if len(st):
                prevMin[i] = max(prevMin[i], diffArr[st[-1]])
            st.append(i)

        # print(diffArr)
        # print(prevMin)

        ans = 0
        for i in range(n):
            ans += diffArr[i] - prevMin[i]
        return ans


    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        st = []
        incrDiff = [0]*n
        for i in range(n):
            incrDiff[i] = max(0, target[i]-nums[i])

        ans = self.find (n, incrDiff)

        decrDiff = [0]*n
        for i in range(n):
            decrDiff[i] = max(0, nums[i]-target[i])
        ans += self.find (n, decrDiff)
        return ans
