class Solution {
    using LL = long long;
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        LL pos = 0, neg = 0;
        nums.insert(nums.begin(), 0), target.insert(target.begin(), 0);
        vector<LL> p(n + 5), d(n + 5);
        for (int i = 1; i <= n; i++) {
            p[i] = target[i] - nums[i];
            d[i] = p[i] - p[i - 1];
            if (d[i] > 0)  pos += d[i];
            else  neg += d[i];
        }
        return max(pos, abs(neg));
    }
};
