class Solution {
    public function minimumOperations($nums, $target) {
        $diff = [];
        for ($i = 0; $i < count($nums); $i++) {
            $diff[] = $target[$i] - $nums[$i];
        }

        $total_operations = 0;
        $current_segment_diff = 0;

        for ($i = 0; $i < count($diff); $i++) {
            if ($i == 0 || ($diff[$i - 1] > 0 && $diff[$i] > 0) || ($diff[$i - 1] < 0 && $diff[$i] < 0)) {
                $additional_operations = abs($diff[$i]) - abs($current_segment_diff);
                if ($additional_operations > 0) {
                    $total_operations += $additional_operations;
                }
                $current_segment_diff = $diff[$i];
            } else {
                $total_operations += abs($diff[$i]);
                $current_segment_diff = $diff[$i];
            }
        }

        return $total_operations;
    }
}

// Example usage:
$solution = new Solution();
$nums = [1, 2, 3];
$target = [2, 3, 4];
echo $solution->minimumOperations($nums, $target); // Example input
