using ll = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> diff(n,0);
        for(int i=0;i<n;i++){
            diff[i] = (target[i]-nums[i]);
        }
        int i=0;
        ll opn=0;
        ll temp=abs(diff[i]);
        ll prevstart=diff[i];
        for(int i=0;i<n;i++){
            ll d = ll(diff[i]*prevstart);
            if(d>0){
                if(abs(diff[i]) >= temp) temp =  abs(diff[i]);
                else{
                    opn += (temp-abs(diff[i]));
                    temp = abs(diff[i]);
                }
            }
            else{
                prevstart=diff[i];
                opn += temp;
                temp=0;
                temp = abs(diff[i]);

            }
        }
        opn += temp;
        return opn;
    }
};
