class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = size(nums);
        vector<long long> diffs(n);
        for (int i = 0; i < n; i++) {
            diffs[i] = target[i]-nums[i];
        }

        long long ans = 0;
        vector<long long> s;
        for (int i = 0; i < n; i++) {
            if (size(s) && s.back()*diffs[i] < 0) {
                ans += abs(s.back());
                s.clear();
                s.push_back(diffs[i]);
            } else {
                long long mx = 0;
                while (size(s) && abs(s.back()) >= abs(diffs[i])) {
                    mx = max(mx,abs(s.back()-diffs[i]));
                    s.pop_back();
                }
                ans += mx;
                s.push_back(diffs[i]);
            }
        }
        ans += abs(s.back());
        return ans;
    }
};
