class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]
        d = [diff[0]] + [diff[i] - diff[i-1] for i in range(1,n)] + [-diff[-1]]
        return sum(x if x > 0 else 0 for x in d)
