class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long inc = 0;
        long long dec = 0;
        long long ans = 0;
        for(int i=0;i<n;i++){
            if(nums[i]==target[i]) {
                inc=0;
                dec=0;
                continue;
            }
            if(inc==0 && dec==0){
                long long fix = abs(nums[i]-target[i]);
                ans += fix;
                if(nums[i]>target[i]) dec = fix;
                else inc = fix;
                continue;
            }
            long long fix = abs(nums[i]-target[i]);
            if(nums[i]>target[i]){
                if(dec==0){
                  dec = fix;
                  ans+=dec;
                  inc = 0;
                  continue;
                }
                else{
                    if(fix<=dec){
                        dec = fix;
                        inc = 0;
                        continue;
                    }
                    else{
                        long long newfix = abs(fix-dec);
                        ans+= newfix;
                        dec = fix;
                        inc=0;
                        continue;
                    }
                }

            }
            if(nums[i]<target[i]){
                if(inc==0){
                 inc = fix;
                 ans+=inc;
                 dec = 0;
                 continue;
                }
                else{
                    if(fix<=inc){
                        inc = fix;
                        dec = 0;
                        continue;
                    }
                    else{
                        long long newfix = abs(fix-inc);
                        ans+= newfix;
                        inc = fix;
                        dec=0;
                        continue;
                    }
                }

            }
        }
        return ans;
    }
};
