class Solution {
public:
    long long getAns(vector<int>&nums,vector<int>&target,int n){
        long long ops=0;
        ops+=(abs(nums[0]-target[0]));
        int inc=(nums[0]<=target[0]),prev_max=ops;
        for(int i=1;i<n;i++){
            int diff=abs(nums[i]-target[i]);
            if(nums[i]<=target[i]){
                if(inc){
                    if(prev_max<=diff){
                        ops+=diff-prev_max;
                        prev_max=diff;
                    }
                    else{
                        prev_max=diff;
                    }
                }
                else{
                    inc^=1;
                    ops+=diff;
                    prev_max=diff;
                }
            }
            else{
                 if(!inc){
                    if(prev_max<=diff){
                        ops+=diff-prev_max;
                        prev_max=diff;
                    }
                    else{
                        prev_max=diff;
                    }
                }
                else{
                    inc^=1;
                    ops+=diff;
                    prev_max=diff;
                }
            }
        }
        return ops;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=getAns(nums,target,n);
        reverse(nums.begin(),nums.end());
        reverse(target.begin(),target.end());
        ans=min(ans,getAns(nums,target,n));
        return ans;
    }
};
