typedef long long LL;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        LL ans = 0;
        for(int l=0, r=0; l<n; l++) {
            int diff = nums[l]-target[l];
            if(diff==0) continue;
            r=l;
            ans += abs(diff);
            int premin = abs(diff), base=abs(diff);
            while(r+1<n && (diff>0==(nums[r+1]-target[r+1]>0))) {
                r++;
                int curdiff = abs(nums[r]-target[r]);
                if(curdiff<=premin) {
                    premin = curdiff;
                    base = curdiff;
                } else {
                    ans += curdiff-premin;
                    premin = curdiff;
                }
            }
            l=r;
        }
        return ans;
    }
};
