class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
       int n = nums.size();
       std::vector<long long> f(n);
       for(int i = 0; i < n; i++) f[i] = 1e14;
       f[0] = std::abs(nums[0] - target[0]);
       for(int i = 1; i < n; i++) {
          long long tmp = nums[i] + target[i - 1] - nums[i - 1];
          if((tmp - target[i]) * (nums[i] - target[i]) <= 0) {
            f[i] = f[i - 1];
            continue;
          }
          f[i] = f[i - 1] + std::min((long long) std::abs(nums[i] - target[i]), (long long) std::abs(tmp - target[i]));
       }
       return f[n - 1];
    }
};
