class Solution {
public:
    long long minimumOperations(vector<int>& n, vector<int>& t) {
        long long ans = 0;
        int cur = 0;
        int s = n.size();
        for (int i=0;i<s;i++)
        {
            int d = t[i]-n[i];
            if (d==0)
            {
                 cur = 0;
            }
            else if (d<0)
            {
                if (cur>=0)
                {
                    ans += abs(d);
                    cur = d;
                }
                else{
                    if (cur<=d)
                    {
                        cur = d;
                    }
                    else{
                        ans += abs(cur-d);
                        cur = d;
                    }
                }
            }
            else{
                if (cur<=0)
                {
                    ans +=d;
                    cur = d;
                }
                else{
                    if (cur>=d){
                        cur = d;
                    }
                    else{
                        ans += d-cur;
                        cur = d;
                    }
                }
            }
            // cout<<i<<" "<<ans<<" "<<cur<<endl;
        }
        // ans += abs(cur);
        return ans;
    }
};

