long long fun(vector<int>diff){
    stack<int>st;
    long long ans=0;
    st.push(abs(diff[0]));
    int n=diff.size();
    for(int i=1;i<n;i++){
        stack<int>st2;
        while(st.size() && st.top()>=abs(diff[i])){
            st2.push(st.top());
            st.pop();
        }
        if(st2.size()){
        int prev=st2.top();
        st2.pop();
        ans+=prev-abs(diff[i]);

        while(st2.size()){
            ans+=st2.top()-prev;
            prev=st2.top();
            st2.pop();
        }
        }

        st.push(abs(diff[i]));
    }
    stack<int>st2;
    while(st.size()){
        st2.push(st.top());
        st.pop();
    }

    int prev=0;
    while(st2.size()){
        ans+=st2.top()-prev;
        prev=st2.top();
        st2.pop();
    }
    return ans;
}

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>diff;
        for(int i=0;i<n;i++){
            diff.push_back(target[i]-nums[i]);
        }   
        int i=0;   
        long long ans=0;
        while(i<n){
            if(diff[i]>=0){
                vector<int>v;
                v.push_back(diff[i]);
                int j=i+1;
                while(j<n && diff[j]>=0){
                    v.push_back(diff[j]);
                    j++;
                }
                ans+=fun(v);
                i=j;
            }
            else{
                vector<int>v;
                v.push_back(diff[i]);
                int j=i+1;
                while(j<n && diff[j]<=0){
                    v.push_back(diff[j]);
                    j++;
                }
                ans+=fun(v);
                i=j;
            }
        }
        return ans;
    }
};
