class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>a(n);
        for(int i=0;i<n;i++){
            int x=nums[i]-target[i];
            if(x>=0)a[i]=1;
            else a[i]=-1;
        }
        long long res=0;
        vector<int>t;
        t.push_back(abs(nums[0]-target[0]));
        for(int i=1;i<n;i++){
            if(a[i]!=a[i-1]){
                res+=1ll*t[0];
                for(int i=1;i<t.size();i++){
                    int x=t[i]-t[i-1];
                    if(x>0)res+=1ll*x;
                }
                t.clear();
            }
            t.push_back(abs(nums[i]-target[i]));
        }
        res+=1ll*t[0];
        for(int i=1;i<t.size();i++){
            int x=t[i]-t[i-1];
            if(x>0)res+=1ll*x;
        }
        t.clear();
        return res;
    }
};
