class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, in = 0, de = 0;
        int n = nums.size();
        for(int i=0;i<n;i++) {
            if(nums[i] > target[i]) {
                int mn = min((long long)nums[i] - target[i], de);
                int more = nums[i] - target[i] - mn;
                ans += more;
                de = mn + more;
                in = 0;
            }else if(nums[i] < target[i]) {
                int mn = min((long long)target[i] - nums[i], in);
                int more = target[i] - nums[i] - mn;
                ans += more;
                in = mn + more;
                de = 0;
            }
            else {
                in = 0, de = 0;
            }

            cout << in << ' ' << de << ' ' << ans << '\n';
        }
        return ans;
    }
};
