class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int i=0;
        long long count=0;
        while(i<n){
            int maxi=0;
            if(nums[i]==target[i]){
                i++;
                continue;
            }
            if(nums[i]<target[i]){
                while(i<n&&nums[i]<target[i]){
                    if(target[i]-nums[i]>maxi){
                        count+=target[i]-nums[i]-maxi;
                        maxi=target[i]-nums[i];
                    }
                    else{
                        maxi=target[i]-nums[i];
                    }
                    i++;
                }
            }
            else{
                while(i<n&&nums[i]>target[i]){
                    if(nums[i]-target[i]>maxi){
                        count+=nums[i]-target[i]-maxi;
                        maxi=nums[i]-target[i];
                    }
                    else{
                        maxi=nums[i]-target[i];
                    }
                    i++;
                }
            }
        }
        return count;
    }
};
