#define ll long long int


class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& tar) {
        priority_queue<pair<ll,int>>pq;
        int n=a.size();
        ll ans=0;

        for(int i=0;i<n;) {
            ll t=tar[i]-a[i];
            if(t>=0) {
                while(i<n && (tar[i]-a[i])>=0) {
                    pq.push({-tar[i]+a[i], i});
                    i++;
                }
            }else {
                while(i<n && (tar[i]-a[i])<0) {
                    pq.push({tar[i]-a[i], i});
                    i++;
                }
            }
            set<int>s;
            while(!pq.empty()) {
                auto val = pq.top();
                pq.pop();
                ll t = 0-val.first;
                int ind = val.second;

                if(s.empty()) {
                    ans+=t;
                }else {
                    ll maxi=0;
                    auto it = s.upper_bound(ind);
                    if(it !=s.end()) {
                        int index = *it;
                        maxi=abs(tar[index]-a[index]);
                    }
                    if(it!=s.begin()) {
                        it--;
                        int index = *it;
                        maxi=max(maxi,(ll)abs(tar[index]-a[index]));
                    }
                    ans+=(t-maxi);
                }
                s.insert(ind);
            }
        }
        return ans;
    }
};
