class Solution {
public:
    int solve(vector<int>& A)  
{  
    // Base Case  
    if (A.size() == 0)  
        return 0;  

    // Initialize ans to first element  
    int ans = A[0];  

    for (int i = 1; i < A.size(); i++) {  

        // For A[i] > A[i-1], operation  
        // (A[i] - A[i - 1]) is required  
        ans += max(A[i] - A[i - 1], 0);  
    }  

    // Return the answer  
    return ans;  
}  

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector <int> arr(n);
        for(int i=0;i<nums.size();i++){
            arr[i] = nums[i]-target[i];
        }
        int i = 0;
        while(i<arr.size()){
            vector <int> temp;
            if(arr[i]<0){
                while(i<n && arr[i]<=0){
                    temp.push_back(abs(arr[i++]));
                }
                ans+=solve(temp);
            }
            else{
                while(i<n && arr[i]>=0){
                    temp.push_back(arr[i++]);
                }
                ans+=solve(temp);
            }
        }
        return ans;
    }
};
