class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size(), res = 0, x = 0, y = n - 1, cur = 0, last = 0;
        vector<long long> cnt(n, 0);
        cnt[0] = nums[0] - target[0];
        res = abs(cnt[0]);
        for(int i = 1; i < n; i++) {
            cnt[i] = nums[i] - target[i];
            if(cnt[i] > 0) {
                if(cnt[i - 1] <= 0) {
                    res += cnt[i];
                }else if(cnt[i - 1] < cnt[i]) {
                    res += cnt[i] - cnt[i - 1];
                }
            }else if(cnt[i] < 0) {
                if(cnt[i - 1] >= 0) {
                    res += -cnt[i];
                }else if(cnt[i - 1] > cnt[i]) {
                    res += cnt[i - 1] - cnt[i];
                }
            }
        }
        return res;
    }
};
