class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        long long ans=0;
        int n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++) v[i]=tar[i]-nums[i];
        int mn=0,mx=0;
        for(int i=0;i<n;i++){
            if(!i){
                if(v[i]<0){
                    ans+=abs(v[i]);
                    mn=abs(v[i]);
                }
                else{
                    ans+=abs(v[i]);
                    mx=abs(v[i]);
                }
                // cout<<i<<' '<<ans<<endl;
                continue;
            }
            if(v[i]<0){
                if(mn>abs(v[i])){
                    mn=abs(v[i]);
                }
                else{
                    ans+=abs(v[i])-mn;
                    mn=abs(v[i]);
                    // mn=max(mn,abs(v[i])-mn);
                }
                mx=0;
            }
            else if(!v[i]){
                mx=0;
                mn=0;
            }
            else{
                if(mx>abs(v[i])){
                    mx=abs(v[i]);
                }
                else{
                    ans+=abs(v[i])-mx;
                    mx=abs(v[i]);
                    // mx=max(mx,abs(v[i])-mx);
                }
                mn=0;
            }
            // cout<<i<<' '<<ans<<endl;
        }
        return ans;
    }
};
