class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n];
        for (int i = 0; i < n; ++i) {
            diff[i] = nums[i] - target[i];
        }
        long ans = Math.abs(diff[0]);
        for (int i = 1; i < n; ++i) {
            ans += Math.abs(diff[i]);
            int d1 = diff[i], d0 = diff[i-1];
            if ((d1 > 0 && d0 > 0) || (d1 < 0 && d0 < 0)) {
                ans -= Math.min(Math.abs(d1), Math.abs(d0));
            }
        }
        return ans;
    }
}
