class Solution {
public:
    long long getAns(vector<long long>& arr, int l, int r) {
        if (l > r) return 0;
        long long ans = 0;
        vector<long long> tmp(r - l + 1);

        map<int, int> mmp;
        for (int i = l; i <= r; i++) {
            tmp[i - l] = abs(arr[i]);
        }
        int last = 0;
        for (int i = 0; i < tmp.size(); i++) {
            if (last < tmp[i]) {
                ans += tmp[i] - last;
            }
            last = tmp[i];
        }


        return ans;
    }
    long long getCnt(vector<long long>& arr, int l, int r) {
        if (l > r) return 0;

        int last = l;
        long long ans = 0;
        for (int i = l; i <= r; i++) {
            if (arr[i] > 0 != arr[last] > 0) {
                ans += getAns(arr, last, i - 1);
                last = i;
            }
        }
        ans += getAns(arr, last, r);
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<long long> arr(n, 0);
        for (int i = 0; i < n; i++) {
            arr[i] = target[i] - nums[i];
        }
        int last = 0;
        for (int i = 0; i < n; i++) {
            if (arr[i] == 0) {
                ans += getCnt(arr, last, i - 1);
                last = i + 1;
            }
        }
        ans += getCnt(arr, last, n - 1);
        return ans;
    }
};
