class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        a.insert(a.begin(), 0);
        b.insert(b.begin(), 0);

        vector<int> c(n + 1);
        vector<int> d(n + 2);
        for (int i = 1; i <= n; i++) {
            c[i] = b[i] - a[i];
            d[i] = c[i] - c[i - 1];
        }

        ll cnt1 = 0, cnt2 = 0;
        for (int i = 1; i <= n; i++) {
            if (d[i] > 0) cnt1 += d[i];
            if (d[i] < 0) cnt2 += -d[i];
        }
        ll ans = cnt1 + cnt2 - min(cnt1, cnt2);
        return ans;
    }
};

