class Solution {
public:

    long long min_operations(vector<long long>& A)  
    {  
        if (A.size() == 0)  
            return 0; 
        long long ans = A[0];  

        for (long long i = 1; i < A.size(); i++) {  
            ans += max(A[i] - A[i - 1], 0LL);  
        }  
        return ans;  
    }  

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> a(nums.size());
        for(long long i=0;i<nums.size();i++){
            a[i]=(target[i]-nums[i]);
        }

        long long n=a.size();

        vector<vector<long long>> change;
        for(long long i=0;i<a.size();i++){
            if(a[i]==0)continue;
            if(a[i]>0){
                vector<long long> curr;
                for(long long j=i;j<n;j++){
                    if(a[j]>0){
                        curr.push_back(a[j]);
                        i=j;
                    }else break;
                }
                change.push_back(curr);
            }else{
                vector<long long> curr;
                for(long long j=i;j<n;j++){
                    if(a[j]<0){
                        curr.push_back(abs(a[j]));
                        i=j;
                    }else break;
                }
                change.push_back(curr);
            }
        }
        long long ans=0;

        for(auto x : change){
            // for(auto y : x)cout<<y<<" ";cout<<"\n";
            ans+= min_operations(x);
        }

        return ans;

    }
};
