class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> a;
        for (int i = 0; i < n; i++)
            a.push_back(target[i] - nums[i]);

        int prev = 0;
        long long res = 0;
        for (int i = 0; i < n; i++) {
            if (prev >= 0 && a[i] >= 0) {
                if (a[i] - prev > 0) 
                    res += a[i] - prev;
            } else if (prev <= 0 && a[i] <= 0) {
                if (prev - a[i] > 0) 
                    res += prev - a[i];
            } else {
                res += abs(a[i]);
            }
            prev = a[i];
        }

        return res;
    }
};
