class Solution:
    def calculateOperations(self, arr: List[int]) -> int:
        if arr[0] < 0:
            return self.calculateOperations([-a for a in arr])
        elif len(arr) == 1:
            return arr[0]
        count = min(arr)
        arr = [a - count for a in arr]
        acc = list()
        for a in arr:
            if a > 0:
                acc.append(a)
            elif len(acc) > 0:
                count += self.calculateOperations(acc)
                acc.clear()
        if len(acc) > 0:
            count += self.calculateOperations(acc)
        return count

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [t - n for n, t in zip(nums, target)]
        acc = list()
        total = 0
        print(diff)
        for i in range(len(diff)):
            if (len(acc) == 0 or acc[0] * diff[i] > 0) and diff[i] != 0:
                acc.append(diff[i])
            elif len(acc) > 0 and acc[0] * diff[i] < 0:
                print(acc)
                total += self.calculateOperations(acc)
                acc.clear()
                acc.append(diff[i])
            elif diff[i] == 0 and len(acc) > 0:
                print(acc)
