class Solution {
    public long minimumOperations(int[] nums, int[] target) 
    {
        long ans=0;

        int inc_avail=0;
        int dec_avail=0;

        int  n = nums.length;

        for(int i=0;i<n;i++)
        {
            int diff = nums[i]-target[i];

            if(diff>0)
            {
                int temp = diff-inc_avail;

                if(temp>0)ans += temp;
                inc_avail=diff;
                dec_avail=0;
            }
            else if(diff<0)
            {
                diff = -diff;

                int temp = diff-dec_avail;

                if(temp>0) ans += temp;

                inc_avail=0;
                dec_avail=diff;
            }
            else 
            {
                inc_avail=0;
                dec_avail=0;
            }
        }
        return ans;
    }
}
