/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const rc = [];
    for (let i = 0; i < nums.length; i++) {
        const t = nums[i] - target[i];
        if (rc[rc.length - 1] !== t) rc.push(t);
    }
    let rt = 0;
    for (let i = 0; i < rc.length;) {
        const c_i = i;
        let t = rc[c_i] >= 0 ? Infinity : -Infinity;
        while (i < rc.length && ((rc[i] >= 0 && rc[c_i] >= 0) || (rc[i] < 0 && rc[c_i] < 0))) {
            if (rc[c_i] >= 0) {
                t = Math.min(t, rc[i]);
            } else {
                t = Math.max(t, rc[i]);
            }
            i++;
        }
        let c = Math.abs(t);
        for (let j = c_i; j < i; j++) {
            if (rc[c_i] >= 0) {
                if (j > c_i) {
                    if (rc[j] <= rc[j - 1]) continue;
                    c += rc[j] - rc[j - 1];
                } else {
                    c += rc[j] - t;
                }
            } else {
                if (j > c_i) {
                    if (rc[j] >= rc[j - 1]) continue;
                    c += rc[j - 1] - rc[j];
                } else {
                    c += t - rc[j];
                }
            }
        }
        rt += c;
    }
    return rt;
};
