class Solution:
    def minimumOperations(self, nums: list[int], tgt: list[int]) -> int:
        dif = [tgt[i] - nums[i] for i in range(len(nums))]
        ops = seg = 0
        for i in range(len(dif)):
            if (not i) or (dif[i - 1] > 0 and dif[i] > 0) or (dif[i - 1] < 0 and dif[i] < 0):
                ops += abs(dif[i]) - abs(seg) if ((abs(dif[i]) - abs(seg)) > 0) else 0
            else:
                ops += abs(dif[i])
            seg = dif[i]
        return ops
